document.addEventListener("DOMContentLoaded", function () {
  fetch(
    "https://api.openweathermap.org/data/2.5/weather?q=LVIV&units=metric&APPID=5d066958a60d315387d9492393935c19"
  )
    .then((e) => e.json())
    .then((e) => {
      (document.getElementById("temp").textContent = e.main.temp),
        (document.getElementById("pressure").textContent = e.main.pressure),
        (document.getElementById("description").textContent =
          e.weather[0].description),
        (document.getElementById("humidity").textContent = e.main.humidity),
        (document.getElementById("speed").textContent = e.wind.speed),
        (document.getElementById("deg").textContent = e.wind.deg),
        (document.getElementById(
          "icon"
        ).src = `https://openweathermap.org/img/w/${e.weather[0].icon}.png`);
    })
    .catch((e) => console.error("Error:", e));
});
